import React, { useState } from 'react';
import { createMuiTheme, makeStyles } from '@material-ui/core/styles';
import { TextField, FormControl, Grid, Select, MenuItem, FormGroup, IconButton } from '@material-ui/core';
import { InputLabel, Input, InputAdornment, Checkbox, FormControlLabel } from '@material-ui/core';
import { Visibility, VisibilityOff } from '@material-ui/icons';

const theme = createMuiTheme({
    overrides: {
        MuiFormControl: {
            fullWidth: true,
            require: true,
        }
    }
});

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
    },
}));

export default function FormContent() {
    const classes = useStyles();
    const [employee, setEmployee] = useState({
        id: '',
        lastName: '',
        firstName: '',
        email: '',
        password: '',
        contact: '',
        position: 'waiter',
        showPassword: false,
    });
    const [emp, setEmp] = React.useState('waiter');

    const handleChange = (prop) => (event) => {
        setEmployee({ ...employee, [prop]: event.target.value });
    };

    const handleClickShowPassword = () => {
        setEmployee({ ...employee, showPassword: !employee.showPassword });
    };

    const handleMouseDownPassword = (event) => {
        event.preventDefault();
    };

    return (
        <div className={classes.root}>
            <Grid container spacing={2}>
                <Grid item xs={12}>
                    <FormControl fullWidth>
                        <TextField
                            autoFocus
                            id="empId"
                            label="Employee ID"
                            type="text"
                            helperText="How to create an ID is taught by the manager"
                            value={employee.id}
                            onChange={handleChange('id')}
                            required
                        />
                    </FormControl>
                </Grid>
                <Grid item xs={6}>
                    <FormControl fullWidth>
                        <TextField
                            id="emplName"
                            label="Last Name"
                            type="text"
                            value={employee.lastName}
                            onChange={handleChange('lastName')}
                            required
                        />
                    </FormControl>
                </Grid>
                <Grid item xs={6}>
                    <FormControl fullWidth>
                        <TextField
                            id="empfName"
                            label="First Name"
                            type="text"
                            value={employee.firstName}
                            onChange={handleChange('firstName')}
                            required
                        />
                    </FormControl>
                </Grid>
                <Grid item xs={6}>
                    <FormControl fullWidth>
                        <TextField
                            id="empEmail"
                            label="Email Address"
                            type="text"
                            value={employee.email}
                            onChange={handleChange('email')}
                            required
                        />
                    </FormControl>
                </Grid>
                <Grid item xs={6}>
                    <FormControl fullWidth>
                        <InputLabel htmlFor="standard-adornment-password">Password</InputLabel>
                        <Input
                            id="standard-adornment-password"
                            type={employee.showPassword ? 'text' : 'password'}
                            value={employee.password}
                            onChange={handleChange('password')}
                            endAdornment={
                                <InputAdornment position="end">
                                    <IconButton
                                        aria-label="toggle password visibility"
                                        onClick={handleClickShowPassword}
                                        onMouseDown={handleMouseDownPassword}
                                    >
                                        {employee.showPassword ? <Visibility /> : <VisibilityOff />}
                                    </IconButton>
                                </InputAdornment>
                            }
                        />
                    </FormControl>
                </Grid>
                <Grid item xs={6}>
                    <FormControl fullWidth>
                        <TextField
                            id="empContact"
                            label="Contact Number"
                            type="text"
                            value={employee.contact}
                            onChange={handleChange('contact')}
                            required
                        />
                    </FormControl>
                </Grid>
                <Grid item xs={6}>
                    <FormControl>
                        <InputLabel>Position</InputLabel>
                        <Select
                            id="demo-simple-select"
                            value={employee.position}
                            onChange={handleChange('position')}
                        >
                            <MenuItem value="waiter">Waiter</MenuItem>
                            <MenuItem value="cashier">Cashier</MenuItem>
                        </Select>
                    </FormControl>
                </Grid>
            </Grid>
        </div>
    );
}
