const loggedIn = (
  Axios({
    method: 'GET',
    withCredentials: true,
    url: "http://localhost:3001/user",
  })
    .then((res) => {
      console.log(res);
      if (res.data) {
        // setChecker(true);
        return res;
      } else {
        return null;
      }
    })
);


ReactDOM.render(
  <React.StrictMode>
    <Router>
      <Box pl={2}>
        {/* Customer POV Links */}
        <Switch>
          {/* Set the links/paths here */}
          {/* <Route exact path="/">
            {loggedIn == true ? Inventory : Home}
          </Route> */}
          {/* <Route path="/" exact component={loggedIn} /> */}
          <Route exact path="/login">
            {loggedIn == true ? <Redirect to="/" /> : Login}
          </Route>
          <Route path="/order" exact component={Order} />
          <Route path="/order_form" exact component={withRouter(Order_Form)} />
        </Switch>
      </Box>
      {/* Employee POV Links */}
      <Switch>
        {/* Set the links/paths here */}
      </Switch>
    </Router>
  </React.StrictMode>,
  document.getElementById('root')
);

