import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

//For Dialog
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Slide from '@material-ui/core/Slide';

//For Card
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';

//Icons
import '../../materials/css/orders.css'
import CheckSharpIcon from '@material-ui/icons/CheckSharp';
import ShareIcon from '@material-ui/icons/Share';

const useStyles = makeStyles((theme) => ({
    root: {
        maxWidth: 345,
    },
    media: {
        height: 0,
        paddingTop: '56.25%', // 16:9
    },
    expand: {
        transform: 'rotate(0deg)',
        marginLeft: 'auto',
        transition: theme.transitions.create('transform', {
            duration: theme.transitions.duration.shortest,
        }),
    },
    expandOpen: {
        transform: 'rotate(180deg)',
    },
    avatar: {
        backgroundColor: '#3f51b5', //Change Avatar Icon
    },
}));

const Transition = React.forwardRef(function Transition(props, ref) {
    return <Slide direction="up" ref={ref} {...props} />;
});

export default function RecipeReviewCard() {
    const classes = useStyles();
    const [open, setOpen] = React.useState(false);

    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    const dialog = () => {

    };

    return (
        <Card className={classes.root}>
            <CardHeader
                avatar={
                    <Avatar aria-label="recipe" className={classes.avatar} />
                }
                title="Ginaling" //Product Name
                subheader="September 14, 2016" //Order Data
            />
            <CardMedia
                className={classes.media}
                image="/img/ginaling.jpeg"
                title="Ginaling"
            />
            <CardContent>
                <Typography variant="body2" color="textPrimary" component="p">
                    <b>Order ID: 4f6as5c16saw546216af84afs9</b>
                    <br />
                    <b>For:</b><Button
                        className="text-button"
                        size="medium"
                        onClick={handleClickOpen}>
                        Allan Jericho
                                </Button>
                    <br />
                    <b>Quantity:</b> 5
                    <br />
                    <b>Orders:</b> Ginaling, 2 Plain Rice, Coke
                </Typography>
                <Dialog
                    open={open}
                    TransitionComponent={Transition}
                    keepMounted
                    onClose={handleClose}
                    aria-labelledby="alert-dialog-slide-title"
                    aria-describedby="alert-dialog-slide-description"
                >
                    <DialogTitle id="alert-dialog-slide-title">{"Use Google's location service?"}</DialogTitle>
                    <DialogContent>
                        <DialogContentText id="alert-dialog-slide-description">
                            Let Google help apps determine location. This means sending anonymous location data to
                            Google, even when no apps are running.
                        </DialogContentText>
                    </DialogContent>
                </Dialog>
            </CardContent>
            <CardActions disableSpacing>
                <IconButton aria-label="checker">
                    <CheckSharpIcon />
                </IconButton>
            </CardActions>
        </Card>
    );
}
