import React, { useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Button, Box, Grid, FormControl, InputLabel, Input } from '@material-ui/core';

//Custom Imports
import Inputs from './input';
import Range from './range';
import Logo from '../Logo(temp)/logo';
import Select from './select';

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1
    },
    margin: {
        margin: theme.spacing(1),
    },
}));

export default function Order_form() {
    const classes = useStyles();
    const [choice, setChoice] = useState('');

    const mainDish = [
        'Ginaling',
        'Pork Sisig',
        'Pork Humba',
        'Pork Siomai'
    ];
    const appetizer = ['Ampalaya w/ Egg'];
    const soup = ["Bird's Nest"];
    const rice = [
        'Plain Rice',
        'Fried Rice w/ Egg'
    ];
    const Beverages = [
        'Coke',
        'Sprite',
        'Royal',
        'Pepsi'
    ];


    const handleChange = (event) => {
        setChoice(event.target.value);
    };

    return (
        <div className={classes.root}>
            <Logo />
            {/* <Box pl={3}>
                <h2>Order Form</h2>
                <form>
                    <FormControl fullWidth className={classes.margin}>
                        <InputLabel htmlFor="my-input">Email</InputLabel>
                        <Input type="email" id="my-input" aria-describedby="my-helper-text" />
                    </FormControl>
                </form>
            </Box> */}
            <Grid container spacing={1}>
                <Grid item xs={12}><h2>Order Form</h2></Grid>
                <Grid item xs={12}>
                    <FormControl fullWidth className={classes.margin}>
                        <InputLabel htmlFor="my-input">Full Name</InputLabel>
                        <Input type="text" id="my-input" aria-describedby="my-helper-text" />
                    </FormControl>
                </Grid>
                <Grid item xs={6}>
                    <FormControl fullWidth className={classes.margin}>
                        <InputLabel htmlFor="my-input">Email</InputLabel>
                        <Input type="email" id="my-input" aria-describedby="my-helper-text" />
                    </FormControl>
                </Grid>
                <Grid item xs={6}>
                    <FormControl fullWidth className={classes.margin}>
                        <InputLabel htmlFor="my-input">Mobile Number</InputLabel>
                        <Input type="text" id="my-input" aria-describedby="my-helper-text" />
                    </FormControl>
                </Grid>
                <Grid item xs={12}><h4>Main Dish</h4></Grid>
                <Grid item xs={12}>
                    <FormControl fullWidth className={classes.margin}>
                        <InputLabel htmlFor="my-input">Full Name</InputLabel>
                        <Input type="text" id="my-input" aria-describedby="my-helper-text" />
                    </FormControl>
                </Grid>
            </Grid>
        </div>
    );
}
