import React, { useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Button, Box, Grid, FormControl } from '@material-ui/core';

//Custom Imports
import Inputs from './input';
import Range from './range';
import Logo from '../Logo(temp)/logo';
import Select from './select';

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        flexWrap: 'wrap',
    },
    margin: {
        margin: theme.spacing(1),
      },
}));

export default function Order_form() {
    const classes = useStyles();
    const [choice, setChoice] = useState('');

    const handleChange = (event) => {
        setChoice(event.target.value);
    };

    return (
        <div className={classes.root}>
            <Logo />
            <Box pl={3}>
                <h2>Order Form</h2>
                <form>
                    <FormControl fullWidth className={classes.margin}>
                        <Input type="text" label="Full Name"/>
                    </FormControl>
                    {/* <FormControl type="text" label="Full Name"/>
                    <FormControl type="email" label="Email Address"/>
                    <FormControl type="text" label="Mobile Number"/> */}
                </form>
            </Box>
            {/* <Grid container spacing={2}>
                <Grid item xs={12}><h2>Order Form</h2></Grid>
                <Grid item xs={12}>
                    <FormControl type="text" label="Full Name"/>
                </Grid>
                <Grid item xs={6}>
                    <FormControl type="email" label="Email Address"/>
                </Grid>
                <Grid item xs={6}>
                    <FormControl type="text" label="Mobile Number"/>
                </Grid>
            </Grid> */}
        </div>
    );
}
