import React, { useState, useEffect } from 'react';
import { FormControl, InputLabel, Input, FormHelperText, FormControlLabel, Checkbox } from '@material-ui/core';
import { InputAdornment, IconButton } from '@material-ui/core';
import { CheckBoxOutlineBlank } from '@material-ui/icons';
import CheckBoxIcon from '@material-ui/icons/CheckBox';
import { Visibility, VisibilityOff } from '@material-ui/icons';

export default function FormCont({ type, label, handle }) {
    const [showPass, setShowPass] = useState(false);

    const handleClickShowPassword = () => {
        setShowPass(!showPass);
    };

    const handleMouseDownPassword = (event) => {
        event.preventDefault();
    };

    // const inputPassword = () => {
    //     return (
    //         <FormControl>
    //             <InputLabel htmlFor="standard-adornment-password">label</InputLabel>
    //             <Input
    //                 id="standard-adornment-password"
    //                 type={showPass ? 'text' : type}
    //                 onChange={handle}
    //                 endAdornment={
    //                     <InputAdornment position="end">
    //                         <IconButton
    //                             aria-label="toggle password visibility"
    //                             onClick={handleClickShowPassword}
    //                             onMouseDown={handleMouseDownPassword}
    //                         >
    //                             {showPass ? <Visibility /> : <VisibilityOff />}
    //                         </IconButton>
    //                     </InputAdornment>
    //                 }
    //             />
    //         </FormControl>
    //     );
    // };

    // const inputEmail = () => {
    //     return (
    //         <div>
    //             <FormControl>
    //                 <InputLabel>{label}</InputLabel>
    //                 <Input type={type} aria-describedby="my-helper-text" onChange={handle} />
    //                 {subtext}
    //             </FormControl>
    //         </div>
    //     );
    // };
    if (type == 'password') {
        var subtext = <FormControlLabel control={<Checkbox
            icon={<CheckBoxOutlineBlank fontSize="small" />}
            checkedIcon={<CheckBoxIcon fontSize="small" />}
            name="checkedC"
            color="primary"
        />}
            label="Remember me" />
    } else {
        var subtext = <FormHelperText id="my-helper-text">We'll never share your email.</FormHelperText>
    }

    return (
        ({ type } == 'password') ?
            (
                <FormControl>
                    <InputLabel htmlFor="standard-adornment-password">label</InputLabel>
                    <Input
                        id="standard-adornment-password"
                        type={showPass ? 'text' : type}
                        onChange={handle}
                        endAdornment={
                            <InputAdornment position="end">
                                <IconButton
                                    aria-label="toggle password visibility"
                                    onClick={handleClickShowPassword}
                                    onMouseDown={handleMouseDownPassword}
                                >
                                    {showPass ? <Visibility /> : <VisibilityOff />}
                                </IconButton>
                            </InputAdornment>
                        }
                    />
                    {subtext}
                </FormControl>
            ) : (
                <FormControl>
                    <InputLabel>{label}</InputLabel>
                    <Input type={type} onChange={handle} />
                    {subtext}
                </FormControl>
            )
    );
}
