import React, { useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Button, Box, Grid } from '@material-ui/core';

//Custom Imports
import FormControl from './form_control';
import Range from './range';
import Logo from '../Logo(temp)/logo';
import Select from './select';

const useStyles = makeStyles((theme) => ({
    formControl: {
        minWidth: 120,
    },
}));

export default function Order_form() {
    const classes = useStyles();
    const [choice, setChoice] = useState('');

    const handleChange = (event) => {
        setChoice(event.target.value);
    };

    return (
        <div>
            <Logo />
            <Box pl={2}>
                <h2>Order Form</h2>
                <form action="" method="post">
                    <Grid container spacing={0}>
                        <Grid item xs={12}>
                            <FormControl type="text" label="ID Number" />
                        </Grid>
                        <Grid item xs={6}>
                            <FormControl type="text" label="Full Name" />
                        </Grid>
                        <Grid item xs={6}>
                            <FormControl type="text" label="Contact Number" />
                        </Grid>
                        <Grid item xs={12}>
                            <FormControl type="email" label="Email Address" />
                        </Grid>
                        <Grid item xs={3}>
                            <Select choice={choice} action={handleChange} />
                        </Grid>
                    </Grid>
                </form>
                <Range />
                <Button variant="contained" type="submit" color="primary">Order</Button>
            </Box>
        </div>
    );
}
